/// <reference types="react" />
import * as React from 'react';


/**头部导航 */
export class Topbar extends React.Component<{
    logo:{
        img?:string,
        text?:string,
        minText?:string,
        alt?:string
    }
},{}>{
    base:any;
    linkState:any;
}

/**则忠风格整体布局 */
export class ZZStyleLayout extends React.Component<{},{}>{
    base:any;
    linkState:any;
}


export type MenuItem = any & {
    text: string, //显示文字
    state: "close" | "open",//打开状态
    iconCls: "fi-folder",//icon
    openMode: "ajax-jsx" | "ajax",//打开方式
    children?:Array<MenuItem> //子菜单
}

export class Sidebar extends React.Component<{
    data:{
        menu:Array<MenuItem>
    }
},{}>{
    base:any;
    linkState:any;
}

export class Tree extends React.Component<{
    onChange:(e)=>void,
    data:Array<MenuItem>
},{}>{
    base:any;
    linkState:any;
}